import React, { Component } from 'react';
import './app.css';

class App extends Component {
    state = {
        isLoading: false,
        data: [],
        results: [],
    }

    onClick = () => {
        console.log('Got Clicked!', this.state.rates);

        this.setState({
            isLoading: true,
        });

        fetch('https://api.exchangeratesapi.io/latest')
            .then(response => response.json())
            .then(data => {

                if (data.message === "No rates") {
                    this.setState({
                        results: [],
                        isLoading: false,
                    });
                    return;
                }


            })
    }
}

export default App;
