import React, { Component } from 'react';
import './Goat.css';
import goatImage from './goat.png';

class Goat extends Component {
  state = {
    count: 0,
  }

  getFed = () => {
    this.setState({
      count: this.state.count + 1,
    });
  }
  render() {
    return (
      <div className="Goat">
        <img src={goatImage} alt="goat" />
        <button onClick={this.getFed}>
          Feed me x{this.state.count}
          </button>
      </div>
    );
  }
}

export default Goat;
