import React, { Component } from 'react';
import './app.css';

class App extends Component {
    state = {
        isLoading: false,
        data: [],
        results: [],
    }

    onClick = () => {
        console.log('Got Clicked!', this.state.rates);

        this.setState({
            isLoading: true,
        });
    }

    componentDidMount() {
        fetch('https://api.exchangeratesapi.io/latest')
            .then(response => response.json())
            .then(data => {

                if (data.message === "No rates") {
                    this.setState({
                        results: [],
                        isLoading: false,
                    });
                    return;
                }
            });
    }



    render() {
        return (
            <div className="graph">
                {
                    this.state.apiData.map(datum => (
                        <div class="graph" style={{height: datum.percentage + "%"}}>
                            {datum.label}
                        </div>
                    ))
                }
            </div>

        );
    }
}

export default App;
