import React, { Component } from 'react';
import './app.css';

class App extends Component {
    state = {
        isLoading: false,
        apiData: [],
        results: [],
        rates: {}
    }

    onClick = () => {
        console.log('Got Clicked!', this.state.rates);

        this.setState({
            isLoading: true,
            apiData: [],
            results: [],
            rates: {}
        });
    }

    componentDidMount() {
        fetch('https://api.exchangeratesapi.io/latest')
            .then(response => response.json())
            .then(data => {
                console.log(data)

                this.setState({
                    rates: data.rates,
                    results: []
                });
            })
    }


    rateKeys() {
        return Object.keys(this.state.rates);
    }


    render() {
        return (
            <div className="graph">
                {
                    this.rateKeys().map(countryCode => (
                        <div class="graph" style={{ height: this.state.rates[countryCode] + "%" }}>
                            {countryCode}{this.state.rates[countryCode]}
                        </div>
                    ))
                }
            </div>

        );
    }
}

export default App;
